import React, { createContext, useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import TodaysMatches from "../components/TodaysMatches";
import { Context } from "../App";

export const IndexContext = createContext();

function IndexPage() {
  const { user, setFollowedMatches } = useState(Context);
  const [matchesData, setMatchesData] = useContext({});
  const [followedMatches] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("/index")
      .then((res) => res.json())
      .then((data) => {
        setMatchesData(data.matches);
        // setFollowedMatches(data.followed_matches);
      });
  }, []);

  useEffect(() => {
    fetch("/get_followed_matches")
      .then((res) => res.json())
      .then((data) => {
        setFollowedMatches(data.followed_matches);
      });
  }, []);

  return (
    <IndexContext.Provider value={{ matchesData, followedMatches }}>
      {<TodaysMatches />}
    </IndexContext.Provider>
  );
}

export default IndexPage;
