import React, { useContext } from "react";
import styled from "styled-components";
import { Context } from "../App";

const LeaguesComponent = styled.div`
  .leagues {
    display: flex;
    flex-direction: column;
    width: 30%;
    min-width: 400px;
    margin: 25px;
    padding: 20px;
    border: 2px solid var(--border_light);
    border-radius: 5px;
    background-color: var(--navbar_color);
    color: #fff;
  }

  .leagues .topping {
    padding: 10px;
    margin: 0;
    color: #fff;
    font-weight: normal;
  }

  .leagues .league {
    display: flex;
    color: rgba(255, 255, 255, 0.8);
    margin: 0px;
    margin-top: 10px;
    padding: 5px;
    border: 2px solid rgba(255, 255, 255, 0.5);
    border-radius: 5px;
    transition: all 0.3s;
  }

  .leagues .league:hover {
    border-color: var(--hover_bg_color);
    color: #fff;
  }

  .leagues .league i {
    color: rgba(255, 255, 255, 0.8);
    margin: auto 10px auto 10px;
    transition: all 0.3s;
  }

  .leagues .league i:hover {
    color: white;
    cursor: pointer;
    text-shadow: 0px 0px 10px #fff;
  }

  .leagues .league .topping {
    font-size: 22px;
    color: rgba(255, 255, 255, 0.8);
    transition: all 0.3s;
  }

  .leagues .league:hover {
    .topping {
      color: #fff;
    }
  }

  .leagues .league .league_logo {
    background-color: white;
    padding: 3px;
    border-radius: var(--radius);
    width: 30px;
    margin: auto 0 auto 5px;
  }
`;

function Leagues({ leagues, title }) {
  const { followedLeagues, follow_league } = useContext(Context);

  const checkInclude = (id) => {
    return typeof followedLeagues === "object"
      ? Number(id) in followedLeagues
      : false;
  };
  return (
    <LeaguesComponent>
      <div className="leagues">
        <h3 className="topping">{title}</h3>
        {Object.keys(leagues).map((league) => (
          <div className="league">
            {checkInclude(league) ? (
              <i
                className="fa-solid fa-star"
                onClick={() => follow_league(league)}
              />
            ) : (
              <i
                className="fa-regular fa-star"
                onClick={() => follow_league(league)}
              />
            )}
            <img
              src={`images/leagues/${leagues[league].slug}/logo.png`}
              alt={`${leagues[league].slug}`}
              className="league_logo"
            ></img>
            <span className="topping">{leagues[league].name}</span>
          </div>
        ))}
      </div>
    </LeaguesComponent>
  );
}

export default Leagues;
