import React, { useCallback, useContext } from "react";
import styled from "styled-components";
import Heading from "../../../components/Heading";
import { MatchDetailsContext } from "../MatchDetails";

const TeamsHeading = styled.div`
  & {
    border-radius: 15px;
    padding: 10px;
    ${(p) =>
      p.color
        ? `background-color: rgba(${p.color[0]}, ${p.color[1]}, ${p.color[2]}, 0.5)`
        : null}
  }
`;

function MatchDetailsHeading() {
  const { match } = useContext(MatchDetailsContext);
  return <Heading title={match.teams.home.name} />;
}

export default MatchDetailsHeading;
