import React from "react";
import Button from "./Button";
import "./styles/LoginForm.css";

const InputBox = ({ correct = false, type }) => {
  return (
    <>
      <input className={"input_box " + correct} type={type ? type : "text"} />
      <i style={{ color: "#fff" }} className="fa-solid fa-user"></i>
    </>);
}

function LoginForm({ correct = false }) {
  console.log(correct)
  return (
    <div className="login_wrapper">
      <h1 className="title">Login</h1>
      <form className="login_form">
        <InputBox />
        <InputBox type="password" />
        <div className="remember_message">
          <p className="remember_me">
            <input type="checkbox" />
            <label>Remember Me</label>
          </p>
          {correct
            ? null
            : <p className="message">Username or passwort is not corretct!</p>}

        </div>
        <Button className="login_btn" variant="light" size="xxl">
          Submit
        </Button>
      </form>
      <p className="register">Don't have an account?<a className="signup" href="/register">Sign Up</a></p>
    </div>
  );
}

export default LoginForm;
