import React, { useContext, useEffect, useState } from "react";
import { MatchDetailsContext } from "../MatchDetails";
import styled from "styled-components";

const MatchLineupsComponent = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 20px;

  .field {
    position: relative;
  }

  img {
    min-height: 150px;
    min-width: 130px;
    max-width: 500px;
  }

  .message {
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    margin: auto;
    width: 180px;
    text-align: center;
    height: 20px;
    font-size: 25px;
    transform: translate(0, -150%);
  }
`;

const TeamComponent = styled.div`
  overflow: hidden;
  display: flex;
  flex-direction: column;
  position: absolute;
  left: 0;
  justify-content: center;
  ${(p) =>
    p.cords
      ? `height: ${(p.cords.bottom - p.cords.top) / 2}px; 
            width: ${p.cords.right - p.cords.left}px;`
      : null}
  ${(p) =>
    p.key === "away"
      ? "top: 0;"
      : `top: ${(p.cords.bottom - p.cords.top) / 2}px;`}
`;

const PlayerComponent = styled.div`
  width: 40px;
  height: 40px;
  line-height: 40px;
  ${(p) => (p.color.color ? `background-color: #${p.color.color};` : null)}
  ${(p) => (p.color.outline ? `outline: 2px solid #${p.color.outline};` : null)}
  ${(p) => (p.color.number ? `color: #${p.color.number};` : null)}
  border-radius: 50%;
  text-align: center;
`;

const LayerComponent = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
`;

function MatchLineups() {
  const { lineups } = useContext(MatchDetailsContext);
  const [layers, setLayers] = useState({});

  useEffect(() => {
    if (Object.keys(lineups).length > 0) {
      setLayers({
        home: lineups.teams.home.formation.split("-"),
        away: lineups.teams.away.formation.split("-"),
      });
    }
  }, [lineups]);

  return (
    <MatchLineupsComponent>
      <div className="field" id="field">
        <img src="\images\pages\matchDetails\field.png" alt="field" />
        {lineups != null &&
        Object.keys(layers).length > 0 &&
        Object.keys(lineups).length > 0 ? (
          Object.keys(lineups.teams).map((team) => (
            <TeamComponent
              className="team"
              id={team}
              key={team}
              cords={document.getElementById("field").getBoundingClientRect()}
              style={
                team === "away"
                  ? { top: "0" }
                  : {
                      top: `${Math.ceil(
                        (document
                          .getElementById("field")
                          .getBoundingClientRect().bottom -
                          document
                            .getElementById("field")
                            .getBoundingClientRect().top) /
                          2
                      )}px`,
                      flexDirection: "column-reverse",
                    }
              }
            >
              <LayerComponent>
                <PlayerComponent
                  team={team}
                  color={lineups.teams[team].player}
                  position={lineups.teams[team].players[0].position}
                >
                  {lineups.teams[team].players[0].number}
                </PlayerComponent>
              </LayerComponent>
              {layers[team].map((layer) => (
                <LayerComponent key={layer}>
                  {Object.keys(lineups.teams[team].players).map((id) => {
                    <PlayerComponent
                      key={id}
                      team={team}
                      color={lineups.teams[team].player}
                    >
                      {lineups.teams[team].players[id].number}
                    </PlayerComponent>;
                  })}
                </LayerComponent>
              ))}
            </TeamComponent>
          ))
        ) : (
          <span className="message">No lineups for this game...</span>
        )}
      </div>
    </MatchLineupsComponent>
  );
}

export default MatchLineups;
