import React, { useContext } from "react";
import { MatchDetailsContext } from "../MatchDetails";
import styled from "styled-components";

const MatchLineupsComponent = styled.div`
  & {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-top: 20px;
  }

  .field {
    position: relative;
  }

  img {
    min-height: 150px;
    min-width: 130px;
    max-width: 400px;
  }
`;

const TeamComponent = styled.div`
  & {
    display: flex;
    position: absolute;
    left: 0;
    ${(p) => console.log(p.id)}
    ${(p) =>
      p.cords
        ? p.id === "home"
          ? `top: 0;`
          : `top: ${(p.cords.bottom - p.cords.top) / 2}px;`
        : null}
    ${(p) =>
      p.cords
        ? `height: ${(p.cords.bottom - p.cords.top) / 2}px; width: ${
            p.cords.right - p.cords.left
          }px;`
        : null}
  }
`;

const PlayerComponent = styled.div`
  & {
    width: 40px;
    height: 40px;
    line-height: 40px;
    background-color: red;
    border-radius: 50%;
    color: #fff;
    text-align: center;
  }
`;

function MatchLineups() {
  const { lineups } = useContext(MatchDetailsContext);
  return (
    <MatchLineupsComponent>
      <div className="field" id="field">
        <img src="\images\pages\matchDetails\field.png" alt="field" />
        {Object.keys(lineups).length > 0
          ? Object.keys(lineups.teams).map((team) => (
              <TeamComponent
                className="team"
                id={team}
                key={team}
                cords={document.getElementById("field").getBoundingClientRect()}
              >
                {Object.keys(lineups.teams[team].players).map((id) => (
                  <PlayerComponent key={id}>
                    {lineups.teams[team].players[id].number}
                  </PlayerComponent>
                ))}
              </TeamComponent>
            ))
          : null}
      </div>
    </MatchLineupsComponent>
  );
}

export default MatchLineups;
