import React, { createContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import TodaysMatches from "../components/TodaysMatches";

export const IndexContext = createContext();

function IndexPage() {
  const [matchesData, setMatchesData] = useState({});
  const [followedMatches, setFollowedMatches] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    fetch("/index")
      .then((res) => res.json())
      .then((data) => {
        setMatchesData(data.matches);
        // setFollowedMatches(data.followed_matches);
      });
  }, []);

  useEffect(() => {
    fetch("/get_followed_matches")
      .then((res) => res.json())
      .then((data) => {
        setFollowedMatches(data.followed_matches);
      });
  }, []);

  const follow_match = (id) => {
    const data = () => {
      return fetch("/follow_match", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ id: id }),
      })
        .then((res) => res.json())
        .thne((res) => res.match);
    };

    if (data === "added") {
      setFollowedMatches((prev) => [...prev, id]);
    } else if (data === "deleted") {
      setFollowedMatches((prev) => [...prev, ([id] = undefined)]);
    }
    console.log(id);
    console.log(data);
    navigate("/");
  };

  return (
    <IndexContext.Provider value={{ matchesData, follow_match }}>
      {<TodaysMatches />}
    </IndexContext.Provider>
  );
}

export default IndexPage;
