import React, { useContext, useEffect, useState } from "react";
import { Context } from "../../App";
import styled from "styled-components";
import Matches from "../../components/Matches";
import Leagues from "../../components/Leagues";

const ProfileComponent = styled.div`
  .profile {
    display: flex;
    z-index: 1;
    margin-left: 25px;
  }
`;

function ProfilePage() {
  const { followedMatches, followedLeagues } = useContext(Context);
  const [matchDetails, setMatchDetails] = useState([]);
  const [leagueDetails, setLeagueDetails] = useState([]);

  useEffect(() => {
    for (const league in followedLeagues) {
      setLeagueDetails((prev) => [
        ...prev,
        { ...followedLeagues[league].details },
      ]);
    }
    for (const match in followedMatches) {
      console.log(match);
      setMatchDetails((prev) => [
        ...prev,
        { ...followedMatches[match].details },
      ]);
    }
    console.log(matchDetails);
    console.log(leagueDetails);
  }, [followedLeagues, followedMatches, matchDetails, leagueDetails]);

  return (
    <ProfileComponent>
      <div className="profile">
        <Leagues
          title="Followed leagues"
          leagues={leagueDetails}
          message="Your followed leagues will apear here!"
        />
        <Matches
          title="Followed matches"
          matches={matchDetails}
          message="Your followed matches will apear here!"
        />
      </div>
    </ProfileComponent>
  );
}

export default ProfilePage;
