import React, { useState } from "react";
import Button from "./Button";
import bcrypt from "bcryptjs";
/* import { AuthData } from "../navigation/AuthWrapper"; */
import "./styles/LoginForm.css";
import useLogin from "../hooks/useLogin";

function LoginForm({ correct = false }) {
  /* const { login } = AuthData(); */

  const [loginFormData, setLoginFormData] = useState({
    username_email: "",
    password: "",
    remember_me: false,
  });

  console.log(loginFormData);

  const [correctInput, setCorrectInput] = useState(false);

  /*  const changeData = (val) => {
    const newData = { ...data };
    if (val.target.id === "remember_me") {
      data[val.target.id] === true
        ? (newData[val.target.id] = false)
        : data[val.target.id] === false
        ? (newData[val.target.id] = true)
        : (newData[val.target.id] = false);
    } else if (val.target.id === "password") {
      newData[val.target.id] = bcrypt.hashSync(
        val.target.value,
        "$2a$10$CwTycUXWue0Thq9StjUM0u"
      );
    } else {
      newData[val.target.id] = val.target.value;
    }
    setData(newData);
  }; */

  const changeData = (event) => {
    const { name, value, type, checked } = event.target;
    if (type === "checkbox") {
      setLoginFormData(() => ({ ...loginFormData, [name]: checked }));
    } else {
      setLoginFormData(() => ({ ...loginFormData, [name]: value }));
    }
  };

  /* const loginUser = async (body) => {
    const data = fetch(`/login`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(body),
    })
      .then((response) => response.json())
      .then((data) => data.correct_input);
    console.log(data);
    if (data) {
      window.location.href = "/";
    } else {
      console.log("sad");
    }
  };
 */
  return (
    <div className="login_wrapper">
      <h1 className="title">Login</h1>
      <form className="login_form">
        <div>
          <input
            id="username_email"
            onChange={changeData}
            placeholder="Username or E-Mail"
            className={"input_box " + correct}
            type="text"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-user"></i>
        </div>
        <div>
          <input
            id="password"
            onChange={changeData}
            placeholder="Password"
            className={"input_box " + correct}
            type="password"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-lock"></i>
        </div>
        <div className="remember_message">
          <div className="remember_me">
            <input onClick={changeData} id="remember_me" type="checkbox" />
            <label htmlFor="remember_me">Remember Me</label>
          </div>
          {correct ? null : (
            <p className="message">Username or passwort is not corretct!</p>
          )}
        </div>
        <Button
          onClick={() => loginUser(data)}
          className="login_btn"
          variant="light"
          size="xxl"
        >
          Submit
        </Button>
      </form>
      <p className="register">
        Don't have an account?
        <a className="signup" href="/register">
          Sign Up
        </a>
      </p>
    </div>
  );
}

export default LoginForm;
