import { createContext, useState } from "react";
import RenderRoutes from "./RenderRoutes";
import React from "react";
import Navbar from "../components/Navbar";

export const Context = createContext();

export const AuthWrapper = () => {
  const [user, setUser] = useState({
    username: "",
    email: "",
    auth: false,
  });
  const [darkMode, setDarkMode] = useState(true);

  const login = async (event) => {
    event.preventDefault();
    setLoginFormData((p) => ({
      ...p,
      password: bcrypt.hashSync(p.password, "$2a$10$CwTycUXWue0Thq9StjUM0u"),
    }));
    fetch(`/login`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(loginFormData),
    })
      .then((response) => response.json())
      .then((data) =>
        data.correct_input ? (window.location.href = "/") : console.log("sad")
      );
    setUser({ ...user, auth: true });
  };

  const logout = () => {
    fetch(`/logout`, { method: "GET" })
      .then((response) => response.json())
      .then((res) =>
        res.logged_out ? (window.location.href = "/") : console.log("Error")
      );
    setUser({ ...user, auth: false });
  };

  const toggleDarkMode = () => {
    setDarkMode((prevMode) => !prevMode);
  };

  return (
    <Context.Provider value={{ user, login, logout }}>
      <>
        <Navbar toggleDarkMode={toggleDarkMode} darkMode={darkMode} />
        <RenderRoutes darkMode={darkMode} />
      </>
    </Context.Provider>
  );
};
