import React, { useEffect, useState } from "react";
import styled from "styled-components";

const RankingComponent = styled.div`
  .ranking_table .heading {
    display: flex;
    background-color: var(--heading_color);
    margin: 0;
    height: 150px;
    color: #fff;
    padding: 0 10% 0 10%;
    align-items: center;
    justify-content: center;
  }

  .ranking_table .heading img {
    height: 50px;
    margin-right: 20px;
  }

  .ranking_table {
    width: 900px;
    margin: 20px auto 0 auto;
    color: #fff;
    background-color: var(--navbar_color);
    padding: 10px;
  }

  .ranking_table th {
    justify-content: space-between;
    margin: 10px;
    text-align: left;
    user-select: none;
    font-size: 15px;
  }

  .ranking_table .arrows {
    width: 30px;
  }

  .ranking_table th .po {
    margin-right: 5px;
  }

  .ranking_table th i {
    color: rgba(255, 255, 255, 0.5);
  }

  .ranking_table tbody tr:hover {
    background-color: rgba(var(--secondary), 0.5);
  }

  .ranking_table .td_item {
    display: flex;
    justify-content: center;
    text-align: center;
  }

  .ranking_table #diff {
    justify-content: space-evenly;
  }

  .ranking_table .country {
    user-select: none;
    color: #fff;
  }

  .ranking_table .item {
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .ranking_table .country a {
    padding: 15px;
    border-radius: 5px;
    margin: auto 0 auto 0;
    cursor: pointer;
    user-select: none;
    text-decoration: none;
  }

  .ranking_table .country .country_icon {
    padding-right: 5px;
    padding-top: 2px;
    width: 20px;
    height: 12.5px;
  }
`;

const TdItemComponent = styled.span`
  ${(p) =>
    p.head[p.j].coloredNumber
      ? p.body[p.i][p.head[p.j].atribute] > 0
        ? { color: "rgb(var(--success))" }
        : p.body[p.i][p.head[p.j].atribute] === 0
        ? { color: "rgb(var(--secondary))" }
        : { color: "rgb(var(--danger))" }
      : null}
  ${(p) =>
    p.background_colors
      ? {
          backgroundColor: `rgb(${p.background_colors[0]}, ${p.background_colors[1]}, ${p.background_colors[2]}, 0.5)`,
        }
      : null}
`;

const Ranking = ({ head, tbody, sorting }) => {
  /* 
  
  head={
    0: { 
      name: "Rank", 
      atribute: "position", --where to get info from api
      sortable: true,
      img: --path-to-folder--,
      background: ["color-folder"] }
  }

  sorting={by: atribute, order: "asc"}

  tbody=[{...}, {...}, {...}, ...]

  */

  const [body, setBody] = useState(tbody);
  useEffect(() => {
    setBody(tbody);
  }, [tbody]);
  const [curColumn, setCurColumn] = useState(sorting.by);
  const [order, setOrder] = useState(sorting.order);

  const sortByColumn = (col) => {
    console.log(body);
    if (curColumn !== col) {
      setCurColumn(col);
      const sorted = [...body].sort((a, b) => (a[col] > b[col] ? 1 : -1));
      setBody(sorted);
      setOrder("desc");
    } else if (order === "asc") {
      const sorted = [...body].sort((a, b) => (a[col] > b[col] ? 1 : -1));
      setBody(sorted);
      setOrder("desc");
    } else if (order === "desc") {
      const sorted = [...body].sort((a, b) => (a[col] < b[col] ? 1 : -1));
      setBody(sorted);
      setOrder("asc");
    }
  };

  const findValue = (obj, array) => {
    if (array) {
      let cur = obj[array[0]];
      let new_array = [...array];
      while (new_array.length > 0 && obj) {
        cur = obj[new_array[0]];
        obj = cur;
        new_array.shift();
      }
      return cur;
    }
  };

  return (
    <RankingComponent>
      <table className="ranking_table">
        <thead>
          <tr>
            {Object.keys(head).map((i) => (
              <th key={i}>
                <span
                  className="po"
                  style={
                    head[i].sortable
                      ? { cursor: "pointer" }
                      : { cursor: "auto" }
                  }
                  onClick={
                    head[i].sortable
                      ? () => sortByColumn(head[i].atribute)
                      : null
                  }
                >
                  {head[i].name}
                </span>
                {head[i].sortable && (
                  <>
                    <i
                      className="fa-solid fa-down-long"
                      style={
                        curColumn === head[i].atribute
                          ? order === "desc"
                            ? { color: "#fff" }
                            : null
                          : null
                      }
                    ></i>
                    <i
                      className="fa-solid fa-up-long"
                      style={
                        curColumn === head[i].atribute
                          ? order === "asc"
                            ? { color: "#fff" }
                            : null
                          : null
                      }
                    ></i>
                  </>
                )}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {Object.keys(body).map((i) => (
            <tr key={i}>
              {Object.keys(head).map((j) => (
                <td key={j}>
                  <TdItemComponent
                    className="country"
                    head={head}
                    body={body}
                    j={j}
                    i={i}
                    background_colors={
                      head[j].background
                        ? findValue(body[i], head[j].background)
                        : null
                    }
                  >
                    {head[j].img ? (
                      <img
                        clasName="country_icon"
                        src={`${head[j].img}/${body[i].img}`}
                      />
                    ) : null}
                    {Array.isArray(head[j].atribute)
                      ? findValue(body[i], head[j].atribute)
                      : body[i][head[j].atribute]}
                  </TdItemComponent>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </RankingComponent>
  );
};

export default Ranking;
