import React, { forceUpdate, useEffect } from "react";
import Button from "./Button";
import "./styles/Navbar.css";
import useAuth from "../hooks/useAuth";

const Logo = () => {
  return (
    <div className="logo">
      <img src="images/favicon.ico" alt="logo" />
      <span>Soccher</span>
    </div>
  );
};

const Links = ({ auth }) => {
  return (
    <ul className="nav_links">
      <li>
        <a href="/">Home</a>
      </li>
      {auth === true ? (
        <li>
          <a href="/my_profile">My Profile</a>
        </li>
      ) : null}
    </ul>
  );
};

const Buttons = ({ auth }) => {
  const logout = () => {
    fetch(`/logout`, { method: "GET" })
      .then((response) => response.json())
      .then((res) =>
        res.logged_out ? (window.location.href = "/") : console.log("Error")
      );
  };
  return (
    <ul className="nav_btns">
      <li>
        <Button size="nav_icon" variant="secondary" outline>
          <i id="drp_icon" className="fa-solid fa-magnifying-glass" />
        </Button>
      </li>
      <li>
        <Button size="nav_icon" variant="secondary" outline>
          <i id="drp_icon" className="fa-solid fa-bars" />
        </Button>
      </li>
      {auth === true ? (
        <li>
          <Button size="icon" variant="danger" onClick={logout} outline>
            <i className="fa-solid fa-right-from-bracket"></i>
          </Button>
        </li>
      ) : (
        <Button
          size="icon"
          variant="success"
          onClick={() => (window.location.href = "/login")}
          outline
        >
          <i className="fa-solid fa-right-to-bracket"></i>
        </Button>
      )}
    </ul>
  );
};

const DropdownMenu = () => {
  return (
    <div className="dropdown">
      <ul className="menu">
        <li>
          <a href="/settings">Settings</a>
          <i className="fa-solid fa-sliders" />
        </li>
        <li>
          <a>Settings</a>
          <i className="fa-solid fa-sliders" />
        </li>
      </ul>
    </div>
  );
};

function Navbar() {
  const auth = useEffect(useAuth(), []);
  return (
    <header className="navbar">
      <Logo />
      <Links auth={auth} />
      <Buttons auth={auth} />
    </header>
  );
}

export default Navbar;
