import React, { createContext, useEffect, useState } from "react";
import TodaysMatches from "../components/TodaysMatches";

export const IndexContext = createContext();

function IndexPage() {
  const [matchesData, setMatchesData] = useState({});

  useEffect(() => {
    fetch("/index")
      .then((res) => res.json())
      .then((data) => setMatchesData(data.matches));
  }, []);

  const follow_match = (id) => {
    console.log(id);

    const data = fetch("/follow_match", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ id: id }),
    })
      .then((res) => res.json())
      .then((data) => {
        return data.match;
      });

    const getData = async () => {
      const match = await data;
      console.log(match);
      return match;
    };

    if (getData() === "added") {
      console.log("add");
      setMatchesData((prevData) => ({
        ...prevData,
        [id]: {
          ...prevData[id],
          followed: true,
        },
      }));
    } else if (getData() === "deleted") {
      console.log("del");
      setMatchesData((prevData) => ({
        ...prevData,
        [id]: {
          ...prevData[id],
          followed: false,
        },
      }));
    }
    console.log(getData());
    console.log("Followed: " + matchesData[id].followed);
  };

  return (
    <IndexContext.Provider value={{ matchesData, follow_match }}>
      {<TodaysMatches />}
    </IndexContext.Provider>
  );
}

export default IndexPage;
