import React from "react";
import "./styles/Button.css";

function Button({ variant, color, children, action }) {
  /* const style = {
    variant: variant,
    color: color,
  }; */
  return (
    <button
      style="background-color: blue;"
      className={variant}
      onClick={action}
    >
      {children}
    </button>
  );
}

export default Button;
