import React, { useContext, useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import Heading from "../../components/Heading";
import styled from "styled-components";
import { Context } from "../../App";
import Button from "../../components/Button";

const ConfirmEmailPageComponent = styled.div`
  & {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
  }

  .message {
    color: #fff;
    background-color: var(--navbar_color);
    padding: 30px;
    margin: 30px;
    border-radius: 10px;
    font-size: 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
  }

  .message h1 {
    margin: 5px;
    padding: 10px;
  }

  .message span {
    margin: 10px;
  }

  button {
    margin-top: 10px;
  }
`;

function ConfirmEmailPage() {
  const { user } = useContext(Context);
  const navigate = useNavigate();
  const url = useLocation();
  const token = url.pathname.slice(15);
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetch(`/auth/confirm_email/${token}`)
      .then((res) => res.json())
      .then((res) => {
        if (res.success) {
          setMessage("confirmed");
        } else {
          setMessage(res.message);
        }
        console.log(res);
      })
      .catch((e) => console.log(e));
  }, [user.auth, token]);

  return (
    <>
      <Heading title="Email Confirmation"></Heading>
      <ConfirmEmailPageComponent>
        <div className="message">
          {message === "confirmed" ? (
            <>
              <h1 className="message">Thanks {user.name}!</h1>
              <span>Your email is now confirmed!</span>
              <Button onClick={() => navigate("/")}>Home</Button>
            </>
          ) : message === "expired" ? (
            <h1 className="message">Your email-verification is expired!</h1>
          ) : message === "already confirmed" ? (
            <h1 className="message">{navigate("/")}</h1>
          ) : (
            message === "token is incorrect" && (
              <h1 className="message">
                Your email verification token is not correct!
              </h1>
            )
          )}
        </div>
      </ConfirmEmailPageComponent>
    </>
  );
}

export default ConfirmEmailPage;
