import React from "react";
import styled from "styled-components";

const ButtonComponent = styled.button`
  position: relative;
  dispaly: inline-flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  text-decoration: none;
  vertical-align: middle;
  cursor: pointer;
  user-select: none;
  color: #fff;
  border-radius: 0.3rem;
  transition: all 0.3s;
  padding: ${(props) =>
    props.size === "sm"
      ? "0 1.1rem; height: 34px; font-size: 15px"
      : props.size === "mid"
      ? "0 1.4rem; height: 37px"
      : props.size === "lg"
      ? "0 1.6rem; height: 40px"
      : ""};
  background-color: ${(props) =>
    props.variant === "primary"
      ? "rgba(var(--button_color_rgb), 0.9)"
      : props.variant === "secondary"
      ? "var(--links_color)"
      : props.variant === "danger"
      ? "rgba(var(--danger))"
      : "; color: var(--navbar_color)"};
  border: 2px solid
    ${(props) =>
      props.outline === true
        ? "var(--links_color); color: var(--links_color); background-color: var(--transparent)"
        : "var(--transparent);"};
  &:hover,
  &:focus {
    background-color: ${(props) =>
      props.variant === "primary"
        ? "var(--button_color)"
        : props.variant === "secondary"
        ? "var(--links_color)"
        : props.variant === "danger"
        ? "rgba(var(--danger))"
        : ""};
    border-color: ${(props) =>
      props.variant === "primary"
        ? "var(--button_color)"
        : props.variant === "secondary"
        ? "var(--links_color)"
        : props.variant === "danger"
        ? "rgba(var(--danger))"
        : ""};
    box-shadow: 0 0 10px 3px
      ${(props) =>
        props.variant === "primary"
          ? "var(--button_color)"
          : props.variant === "secondary"
          ? ""
          : props.variant === "danger"
          ? "rgba(var(--danger))"
          : ""};
    color: white;
  }
`;

function Button({
  type,
  variant,
  outline = false,
  className,
  id,
  onClick,
  size,
  children,
}) {
  return (
    <ButtonComponent
      type={type ? type : "button"}
      variant={variant}
      className={className ? `btn_component ${className}` : "btn_component"}
      id={id}
      onClick={onClick}
      size={size}
      outline={outline}
    >
      {children}
    </ButtonComponent>
  );
}

export default Button;
