import styled from "styled-components";

const InputComponent = styled.input`
  border: 2px solid var(--border);
  border-radius: var(--radius);
  background-color: ${(props) =>
    props.type === "text" || props.type === "password" || props.type === "email"
      ? "var(--transparent)"
      : props.type === "search"
      ? "#fff"
      : ""};
  ${(props) =>
    props.size === "sm"
      ? "padding: ;height: 34px; font-size: 16px"
      : props.size === "mid"
      ? "padding: ;height: 37px; font-size: 18px"
      : props.size === "lg"
      ? "padding: ;height: 40px; font-size: 20px"
      : props.size === "xl"
      ? "padding: ;height: 43px; font-size: 22px"
      : props.size === "xxl"
      ? "padding: ;height: 46px; font-size: 24px"
      : console.log("Input: variant")};
  :focus,
  :hover {
  }
`;

const Input = ({ type = "text", children, size }) => {
  return (
    <InputComponent type={type} size={size}>
      {children}
    </InputComponent>
  );
};

export default Input;
