import { useEffect, useState } from "react";

export function useRequest(url) {
  const [user, setUser] = useState({});
  useEffect(() => {
    fetch(url)
      .then((res) => res.json())
      .then((res) => setUser(res.data))
      .catch((e) => console.log(e));
  }, [user.auth]);
  return user;
}
