import React from "react";
import styled from "styled-components";

const ButtonComponent = styled.button`
  position: relative;
  dispaly: inline-flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  text-decoration: none;
  vertical-align: middle;
  cursor: pointer;
  user-select: none;
  border-radius: 0.3rem;
  padding: ${(props) =>
    props.size === "sm"
      ? "1.1rem; height: 34px;"
      : props.size === "mid"
      ? "1.4rem; height: 37px;"
      : props.size === "lg"
      ? "1.6rem; height: 40px;"
      : "1.1rem; height: 34px;"};
`;

function Button({ type, variant, className, id, onClick, size, children }) {
  return (
    <ButtonComponent
      type={type ? type : "button"}
      variant={variant}
      className={className ? `btn_component ${className}` : "btn_component"}
      id={id}
      onClick={onClick}
      size={size}
    >
      {children}
    </ButtonComponent>
  );
}

export default Button;
