import React, { useState, useContext } from "react";
import Button from "./Button";
import bcrypt from "bcryptjs";
import { Context } from "../App";
import "./styles/LoginForm.css";
import { useNavigate } from "react-router-dom";

function LoginForm() {
  const { login } = useContext(Context);
  const navigate = useNavigate();

  const [loginFormData, setLoginFormData] = useState({
    username_email: "",
    password: "",
    remember_me: false,
  });

  const [correctInput, setCorrectInput] = useState(true);

  const changeData = (event) => {
    const { id, value, type, checked } = event.target;
    setLoginFormData(() => ({
      ...loginFormData,
      [id]: type === "checkbox" ? checked : value,
    }));
  };

  const loginUser = (event) => {
    event.preventDefault();
    setLoginFormData((p) => ({
      ...p,
      password: bcrypt.hashSync(p.password, "$2a$10$CwTycUXWue0Thq9StjUM0u"),
    }));
    fetch(`/login`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(loginFormData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.correct_input) {
          navigate("/");
          setCorrectInput(true);
          login();
        } else {
          setCorrectInput(false);
        }
      });
  };

  return (
    <div className="login_wrapper">
      <h1 className="title">Login</h1>
      <form className="login_form">
        <div>
          <input
            id="username_email"
            onChange={changeData}
            placeholder="Username or E-Mail"
            className={"input_box " + correctInput}
            type="text"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-user"></i>
        </div>
        <div>
          <input
            id="password"
            onChange={changeData}
            placeholder="Password"
            className={"input_box " + correctInput}
            type="password"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-lock"></i>
        </div>
        <div className="remember_message">
          <div className="remember_me">
            <input onClick={changeData} id="remember_me" type="checkbox" />
            <label htmlFor="remember_me">Remember Me</label>
          </div>
          {!correctInput && (
            <p className="message">Invalid username/e-mail or passwort!</p>
          )}
        </div>
        <Button
          onClick={loginUser}
          className="login_btn"
          variant="light"
          size="xxl"
        >
          Submit
        </Button>
      </form>
      <p className="register">
        Don't have an account?
        <span className="signup" onClick={() => navigate("/register")}>
          Sign Up
        </span>
      </p>
    </div>
  );
}

export default LoginForm;
