import React, { createContext, useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import MatchDetailsHeading from "./components/MatchDetailsHeading";
import MatchLineups from "./components/MatchLineups";

export const MatchDetailsContext = createContext();

function MatchDetails() {
  const url = useLocation();
  const match_id = url.pathname.slice(15);

  const [match, setMatch] = useState({});
  const [statistics, setStatistics] = useState({});
  const [lineups, setLineups] = useState({});
  const [layers, setLayers] = useState({});

  useEffect(() => []);

  useEffect(() => {
    fetch(`/match_details/${match_id}`)
      .then((res) => res.json())
      .then((res) => {
        setMatch(res.match);
        setStatistics(res.statistics);
        setLineups(res.lineups);
        setLayers({
          home: lineups.teams.home.formation.split("-"),
          away: lineups.teams.away.formation.split("-"),
        }),
          console.log(res);
      });
  }, [match_id]);

  return (
    <MatchDetailsContext.Provider value={{ match, statistics, lineups }}>
      <MatchDetailsHeading />
      <MatchLineups />
    </MatchDetailsContext.Provider>
  );
}

export default MatchDetails;
