import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import Ranking from "../../components/Ranking";
import styled from "styled-components";

const LeagueComponent = styled.div``;

function LeaguePage() {
  const url = useLocation();
  const league_id = url.pathname.slice(8);

  const [standings, setStandings] = useState({});
  const [leagueInfo, setLeagueInfo] = useState({});

  useEffect(() => {
    fetch("/league", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ league_id: league_id }),
    })
      .then((res) => res.json())
      .then((res) => {
        setStandings(res.standings);
        setLeagueInfo(res.league);
      });
  }, [league_id]);
  return (
    <LeagueComponent>
      <Ranking
        head={{
          0: { name: "Rank", sortable: false },
          1: { name: "Team", sortable: false },
          2: { name: "Points", sortable: false },
        }}
        body={standings}
      />
      <>{leagueInfo.name}</>
    </LeagueComponent>
  );
}

export default LeaguePage;
