import React, { useEffect, useState } from "react";
import AdminTable from "../components/AdminTable";
import Heading from "../../../components/Heading";
import Button from "../../../components/Button";

function DashboardPage() {
  const [data, setData] = useState({});

  useEffect(() => {
    fetch("admin/dashboard")
      .then((res) => res.json())
      .then((d) => setData(d.users));
  }, []);

  const [showEditUser, setShowEditUser] = useState(false);
  const [showCreateUser, setShowCreateUser] = useState(false);

  const [editUser, setEditUser] = useState();
  const [editFormData, setEditFormData] = useState();
  const [editCorrectInput, setEditCorrectInput] = useState();

  const [createFormData, setCreateFormData] = useState();
  const [createCorrectInput, setCreateCorrectInput] = useState();

  const toggleCreateUser = () => {
    setShowCreateUser((prev) => !prev);
    setShowEditUser(false);
    setCreateFormData({
      id: user.id,
      username: user.username,
      admin: user.admin,
      email: user.email,
      email_confirmed: user.email_confirmed,
    });
    setCreateCorrectInput({
      username: { unique: true, rules: true },
      email: { unique: true, rules: true },
      password: true,
      confirm_password: true,
    });
  };

  const toggleEditUser = (user) => {
    setShowEditUser((prev) => !prev);
    setShowCreateUser(false);
    if (user) {
      setEditUser(user);
      setEditFormData({
        id: user.id,
        username: user.username,
        admin: user.admin,
        email: user.email,
        email_confirmed: user.email_confirmed,
      });
    }
    setEditCorrectInput({
      username: { unique: true, rules: true },
      email: { unique: true, rules: true },
      password: true,
      confirm_password: true,
    });
  };

  return (
    <div className="ranking">
      <Heading title="Admin Dashboard">
        <Button onClick={() => toggleCreateUser()}>Create User</Button>
      </Heading>
      {!showEditUser && !showCreateUser ? (
        <AdminTable
          head={{
            2: { name: "Id", atribute: "id", sortable: true },
            3: {
              name: "Username",
              atribute: "username",
              sortable: true,
            },
            4: { name: "Email", atribute: "email", sortable: true },
            6: {
              name: "Followed Leagues",
              atribute: "followed_leagues",
              sortable: false,
            },
            5: {
              name: "Followed Matches",
              atribute: "followed_matches",
              sortable: false,
            },
            7: {
              name: "Admin",
              atribute: "admin",
              sortable: true,
            },
            8: {
              name: "Confirmed Email",
              atribute: "email_confirmed",
              sortable: true,
            },
          }}
          sorting={{ by: "id", order: "desc" }}
          tbody={data}
          settings={{
            info: "info",
            delete: "admin/delete/user",
            update: "admin/dashboard",
            data: "users",
          }}
        />
      ) : showEditUser ? (
        <span>penis</span>
      ) : (
        <span>penis2</span>
      )}
    </div>
  );
}

export default DashboardPage;
