import React from "react";
import Navbar from "./components/Navbar";
import IndexPage from "./pages/IndexPage";
import LogoutPage from "./pages/LogoutPage";
import LoginPage from "./pages/LogoutPage";
import "./components/styles/index.css";
import { useState, createContext } from "react";
import { Routes, Route, BrowserRouter } from "react-router-dom";

export const Context = createContext();

const ProtectedRoute = () => {
  if (!user) {
    return <Navigate to="/" replace />;
  }
};

const App = () => {
  const [user, setUser] = useState({
    username: "",
    email: "",
    auth: false,
  });
  console.log(user.auth);

  const [darkMode, setDarkMode] = useState(false);

  const login = () => {
    setUser({ ...user, auth: true });
    console.log("Authenticated: " + user.auth);
  };

  const logout = () => {
    fetch(`/logout`, { method: "GET" })
      .then((response) => response.json())
      .then((res) =>
        res.logged_out ? (window.location.href = "/") : console.log("Error")
      );
    setUser({ ...user, auth: false });
    console.log("Authenticated: " + user.auth);
  };

  const toggleDarkMode = () => {
    setDarkMode((prevMode) => !prevMode);
    console.log("Dark Mode: " + darkMode);
  };

  return (
    <Context.Provider value={{ user, login, logout, darkMode, toggleDarkMode }}>
      <Navbar />
      <BrowserRouter>
        <Routes>
          <Route index element={<IndexPage />} />
          <Route path="login" element={<LoginPage />} />
          <Route path="logout" element={<LogoutPage />} />
          <Route path="my_profile" element={<IndexPage />} />
          <Route path="*" element={<p>There's nothing here: 404!</p>} />
        </Routes>
      </BrowserRouter>
    </Context.Provider>
  );
};

export default App;
