import React, { useState } from "react";
import Button from "./Button";
import bcrypt from 'bcryptjs';
import { Axios } from "axios";
import "./styles/LoginForm.css";

const salt = bcrypt.genSaltSync(10)

function LoginForm({ correct = false }) {
  const [data, setData] = useState({
    username_email: "",
    password: "",
    remember_me: false,
  });

  const changeData = (val) => {
    const newData = { ...data }
    if (val.target.id === "remember_me") {
      data[val.target.id] === true
        ? newData[val.target.id] = false
        : data[val.target.id] === false
          ? newData[val.target.id] = true
          : newData[val.target.id] = false

    } else if (val.target.id === "password") {
      newData[val.target.id] = bcrypt.hashSync(val.target.value, '$2a$10$CwTycUXWue0Thq9StjUM0u')
    }
    setData(newData)
    console.log(newData)
  }

  const postData = (a) => {
    a.preventDefault()
    Axios.post("/login", {
      username_email: data.username_email,
      passwort: data.passwort
    }).then(res => console.log(res))
  }

  return (
    <div className="login_wrapper">
      <h1 className="title">Login</h1>
      <form className="login_form">
        <div>
          <input
            id="username_email"
            onChange={(e) => changeData(e)}
            placeholder="Username or E-Mail"
            className={"input_box " + correct}
            type="text"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-user"></i>
        </div>
        <div>
          <input
            id="password"
            onChange={(e) => changeData(e)}
            placeholder="Password"
            className={"input_box " + correct}
            type="password"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-lock"></i>
        </div>
        <div className="remember_message">
          <div className="remember_me">
            <input onClick={e => changeData(e)} id="remember_me" type="checkbox" />
            <label htmlFor="remember_me">Remember Me</label>
          </div>
          {correct ? null : (
            <p className="message">Username or passwort is not corretct!</p>
          )}
        </div>
        <Button onClick={postData} className="login_btn" variant="light" size="xxl">
          Submit
        </Button>
      </form>
      <p className="register">
        Don't have an account?
        <a className="signup" href="/register">
          Sign Up
        </a>
      </p>
    </div>
  );
}

export default LoginForm;
