import styled from "styled-components";

const InputComponent = styled.input`
  border: 2px solid var(--border);
  border-radius: var(--radius);
  background-color: ${(props) =>
    props.type === "text" || props.type === "password" || props.type === "email"
      ? "var(--transparent)"
      : props.type === "search"
      ? "#fff"
      : ""};
  color: ${(props) =>
    props.variant === "sm"
      ? "padding: 5px; height: 20px; font-size: 16px;"
      : props.variant === "mid"
      ? "padding: 5px; height: 25px; font-size: 18px;"
      : props.variant === "lg"
      ? "padding: 5px; height: 30px; font-size: 20px;"
      : props.variant === "xl"
      ? "padding: 5px; height: 35px; font-size: 22px;"
      : props.variant === "xxl"
      ? "padding: 5px; height: 40px; font-size: 24px;"
      : console.log("Input: variant")};
  ${(props) =>
    props.size === "sm"
      ? "padding: 5px; height: 20px; font-size: 16px;"
      : props.size === "mid"
      ? "padding: 5px; height: 25px; font-size: 18px;"
      : props.size === "lg"
      ? "padding: 5px; height: 30px; font-size: 20px;"
      : props.size === "xl"
      ? "padding: 5px; height: 35px; font-size: 22px;"
      : props.size === "xxl"
      ? "padding: 5px; height: 40px; font-size: 24px;"
      : console.log("Input: variant")}
  :focus,
  :hover {
  }
`;

const Input = ({ type = "text", children, size, variant }) => {
  return (
    <InputComponent type={type} size={size}>
      {children}
    </InputComponent>
  );
};

export default Input;
