import { BrowserRouter } from "react-router-dom";
import RenderRoutes from "./navigation/RenderRoutes";
import "./components/styles/index.css";
import { useState, createContext } from "react";

export const Context = createContext();

function App() {
  const [user, setUser] = useState({
    username: "",
    email: "",
    auth: false,
  });

  const [darkMode, setDarkMode] = useState(true);

  const login = () => {
    setUser({ ...user, auth: true });
  };

  const logout = () => {
    fetch(`/logout`, { method: "GET" })
      .then((response) => response.json())
      .then((res) =>
        res.logged_out ? (window.location.href = "/") : console.log("Error")
      );
    setUser({ ...user, auth: false });
  };

  const toggleDarkMode = () => {
    setDarkMode((prevMode) => !prevMode);
  };

  return (
    <Context.Provider value={{ user, login, logout, darkMode, toggleDarkMode }}>
      <BrowserRouter>
        <AuthWrapper />
      </BrowserRouter>
    </Context.Provider>
  );
}

export default App;
