import React, { useState } from "react";
import Button from "./Button";
import "./styles/LoginForm.css";

const InputBox = ({ correct = false, type, icon, placeholder }) => {
  return (
    <div>
      <input
        placeholder={placeholder}
        className={"input_box " + correct}
        type={type ? type : "text"}
      />
      <i style={{ color: "#fff" }} className={icon}></i>
    </div>
  );
};

function LoginForm({ correct = false }) {
  const url = "";
  const [data, setData] = useState({
    username: "",
    email: "",
    password: "",
  });
  return (
    <div className="login_wrapper">
      <h1 className="title">Login</h1>
      <form className="login_form">
        <div>
          <input
            placeholder="Username or E-Mail"
            className={"input_box " + correct}
            type="text"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-user"></i>
        </div>
        <div>
          <input
            placeholder="Username or E-Mail"
            className={"input_box " + correct}
            type="password"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-lock"></i>
        </div>
        <div className="remember_message">
          <div className="remember_me">
            <input id="remember_me" type="checkbox" />
            <label for="remember_me">Remember Me</label>
          </div>
          {correct ? null : (
            <p className="message">Username or passwort is not corretct!</p>
          )}
        </div>
        <Button className="login_btn" variant="light" size="xxl">
          Submit
        </Button>
      </form>
      <p className="register">
        Don't have an account?
        <a className="signup" href="/register">
          Sign Up
        </a>
      </p>
    </div>
  );
}

export default LoginForm;
