import React, { useContext, useEffect, useState, createContext } from "react";
import Navbar from "./components/Navbar/Navbar";
import IndexPage from "./pages/index/IndexPage";
import LogoutPage from "./pages/logout/LogoutPage";
import LoginPage from "./pages/signIn/LoginPage";
import RegisterPage from "./pages/signUp/RegisterPage";
import MyProfilePage from "./pages/profile/MyProfilePage";
import CookiesRequest from "./components/CookiesRequest";
import "./index.css";
import { Routes, Route, BrowserRouter, Navigate } from "react-router-dom";
import MatchDetails from "./pages/matchDetails/MatchDetails";
import CountryRankingPage from "./pages/countryRanking/CountryRankingPage";
import Matches from "./components/Matches";

export const Context = createContext();

const ProtectedRoute = ({ anti = false, children }) => {
  const { user } = useContext(Context);
  if (user.auth && anti) {
    return <Navigate to="/" replace />;
  } else if (!user.auth && anti === false) {
    return <Navigate to="/" replace />;
  }
  return children;
};

const App = () => {
  const [user, setUser] = useState({ auth: false });
  const addFollow = (id, details) => {
    setUser((prevData) => ({
      ...prevData,
      followed_matches:
        user.followed_matches === undefined
          ? { [Number(id)]: details }
          : { ...prevData.followed_matches, [Number(id)]: details },
    }));
  };
  const deleteFollow = (id) => {
    delete user.followed_matches[id];
    setUser((prevData) => ({
      ...prevData,
      followed_matches: prevData.followed_matches,
    }));
  };
  const follow_match = (id, details = {}) => {
    fetch("/follow_match", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ id: id, details: details }),
    })
      .then((res) => res.json())
      .then((res) => {
        res.state === "added"
          ? addFollow(id, details)
          : res.state === "deleted"
          ? deleteFollow(id)
          : console.log("Auth");
      })
      .catch((error) => console.error(error));
  };
  const setFollowedMatches = () => {
    fetch("/auth")
      .then((res) => res.json())
      .then((data) => setUser(data));
  };
  useEffect(() => {
    fetch("/auth")
      .then((res) => res.json())
      .then((data) => setUser(data));
  }, [user.auth || user.followed_matches]);
  console.log(user.followed_matches);

  const login = () => {
    setUser({ ...user, auth: true });
    console.log("Authenticated: true");
  };
  const logout = () => {
    fetch("/logout")
      .then((res) => res.json())
      .then((data) =>
        data.logged_out
          ? setUser({ ...user, auth: false })
          : console.log("Unexpected Error")
      );
    console.log("Authenticated: false");
  };

  const getCookie = (name) =>
    document.cookie.match("(^|;)\\s*" + name + "\\s*=\\s*([^;]+)")?.pop() || "";

  const [showCookiesRequset, setShowCookiesRequest] = useState(true);
  useEffect(() => {
    setShowCookiesRequest(getCookie("darkmode") !== "" ? false : true);
  }, []);
  const toggleCookiesRequest = () => {
    setShowCookiesRequest((prev) => !prev);
  };

  const [darkmode, setDarkmode] = useState(
    getCookie("darkmode") !== "" ? getCookie("darkmode") : "darkmode"
  );

  const toggleDarkmode = () => {
    setDarkmode((prevMode) =>
      prevMode === "darkmode" ? "lightmode" : "darkmode"
    );
    const cookie = getCookie("darkmode");
    if (cookie !== "") {
      document.cookie = `darkmode=${
        darkmode === "darkmode" ? "lightmode" : "darkmode"
      }`;
    }
  };

  return (
    <BrowserRouter>
      <Context.Provider
        value={{
          user,
          login,
          logout,
          darkmode,
          toggleDarkmode,
          addFollow,
          deleteFollow,
          follow_match,
          showCookiesRequset,
          toggleCookiesRequest,
        }}
      >
        <Navbar />
        <div
          className={darkmode === "darkmode" ? "routes" : "routes_lightmode"}
        >
          <Routes>
            <Route path="/" element={<IndexPage />} />
            <Route
              path="login"
              element={
                <ProtectedRoute anti={true}>
                  <LoginPage />
                </ProtectedRoute>
              }
            />
            <Route
              path="logout"
              element={
                <ProtectedRoute>
                  <LogoutPage />
                </ProtectedRoute>
              }
            />
            <Route
              path="signup"
              element={
                <ProtectedRoute anti={true}>
                  <RegisterPage />
                </ProtectedRoute>
              }
            />
            <Route
              path="profile"
              element={
                <ProtectedRoute>
                  <MyProfilePage />
                </ProtectedRoute>
              }
            />
            <Route path="about" element={<IndexPage />} />
            <Route path="match_details/:match_id" element={<MatchDetails />} />
            <Route path="country_ranking" element={<CountryRankingPage />} />
            <Route
              path="test"
              element={
                <Matches
                  title="test"
                  matches={user.followed_matches}
                  message="No data"
                />
              }
            />
            <Route
              path="*"
              element={<h1>ERROROROROOROROR STOP STOP STOP STOP STOP!!!!!!</h1>}
            />
          </Routes>
        </div>

        <img src="images/logo512.png" alt="background" className="background" />
        {showCookiesRequset && <CookiesRequest />}
      </Context.Provider>
    </BrowserRouter>
  );
};

export default App;
