import React from "react";
import styled from "styled-components";

const RankingComponent = styled.div`
  .ranking_table .heading {
    display: flex;
    background-color: var(--heading_color);
    margin: 0;
    height: 150px;
    color: #fff;
    padding: 0 10% 0 10%;
    align-items: center;
    justify-content: center;
  }

  .ranking_table .heading img {
    height: 50px;
    margin-right: 20px;
  }

  .ranking_table {
    width: 900px;
    margin: 20px auto 0 auto;
    color: #fff;
    background-color: var(--navbar_color);
    padding: 10px;
  }

  .ranking_table th {
    justify-content: space-between;
    margin: 10px;
    text-align: left;
    user-select: none;
    font-size: 15px;
  }

  .ranking_table .arrows {
    width: 30px;
  }

  .ranking_table th .po {
    cursor: pointer;
    margin-right: 5px;
  }

  .ranking_table th i {
    color: rgba(255, 255, 255, 0.5);
  }

  .ranking_table tbody tr:hover {
    background-color: rgba(var(--secondary), 0.5);
  }

  .ranking_table .td_item {
    display: flex;
    justify-content: center;
    text-align: center;
  }

  .ranking_table #diff {
    justify-content: space-evenly;
  }

  .ranking_table .country {
    user-select: none;
    color: #fff;
  }

  .ranking_table .item {
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .ranking_table .country a {
    padding: 15px;
    border-radius: 5px;
    margin: auto 0 auto 0;
    cursor: pointer;
    user-select: none;
    text-decoration: none;
  }

  .ranking_table .country .country_icon {
    padding-right: 5px;
    padding-top: 2px;
    width: 20px;
    height: 12.5px;
  }
`;

const Ranking = ({ head, body }) => {
  /* 
  
  head={
    0: { name: "Rank", atribute: "position", sortable: false },
    1: { name: "Team", atribute: ["team", "name"], sortable: false },
    2: { name: "Points", atribute: "points", sortable: false },
  }

  */

  function sortTableByColumn(table, column, asc = true) {
    const dirModifier = asc ? 1 : -1;
    const tBody = table.tBodies[0];
    const rows = Array.from(tBody.querySelectorAll("tr"));

    console.log(rows);

    // Sort each Row
    const sortedRows = rows.sort((a, b) => {
      let aColTest = a
        .querySelector(`td:nth-child(${column + 1})`)
        .textContent.trim();
      let bColTest = b
        .querySelector(`td:nth-child(${column + 1})`)
        .textContent.trim();

      if (head[column].sort_by === "string") {
        return aColTest > bColTest ? 1 * dirModifier : -1 * dirModifier;
      } else if (head[column].sort_by === "number") {
        return parseFloat(aColTest) > parseFloat(bColTest)
          ? 1 * dirModifier
          : -1 * dirModifier;
      }
    });

    // Remove all existing Trs from the table
    while (tBody.firstChild) {
      tBody.removeChild(tBody.firstChild);
    }

    // Read the newly sorted rows
    tBody.append(...sortedRows);

    // Remember how the column is currently sorted
    table
      .querySelectorAll("th")
      .forEach((th) => th.classList.remove("asc", "desc"));
    table
      .querySelector(`th:nth-child(${column + 1})`)
      .classList.toggle("asc", asc);
    table
      .querySelector(`th:nth-child(${column + 1})`)
      .classList.toggle("desc", !asc);
  }

  document.querySelectorAll(".ranking_table th").forEach((headerCell) => {
    headerCell.addEventListener("click", () => {
      const tableElement = headerCell.parentElement.parentElement.parentElement;
      const headerIndex = Array.prototype.indexOf.call(
        headerCell.parentElement.children,
        headerCell
      );
      const currentIsAscending = headerCell.classList.contains("asc");

      if (
        headerIndex !== 5 &&
        headerIndex !== 3 &&
        headerIndex !== 2 &&
        currentIsAscending
      ) {
        document.querySelectorAll(".fa-up-long").forEach((arrow) => {
          arrow.style.color = "rgba(255, 255, 255, .5)";
        });
        document.querySelectorAll(".fa-down-long").forEach((arrow) => {
          arrow.style.color = "rgba(255, 255, 255, .5)";
        });
        headerCell.querySelectorAll(".fa-up-long").forEach((arrow) => {
          arrow.style.color = "rgb(255, 255, 255)";
        });
      } else if (
        headerIndex !== 5 &&
        headerIndex !== 3 &&
        headerIndex !== 2 &&
        !currentIsAscending
      ) {
        document.querySelectorAll(".fa-up-long").forEach((arrow) => {
          arrow.style.color = "rgba(255, 255, 255, .5)";
        });
        document.querySelectorAll(".fa-down-long").forEach((arrow) => {
          arrow.style.color = "rgba(255, 255, 255, .5)";
        });
        headerCell.querySelectorAll(".fa-down-long").forEach((arrow) => {
          arrow.style.color = "rgb(255, 255, 255)";
        });
      }

      switch (headerIndex) {
        case 0:
        case 1:
        case 4:
          sortTableByColumn(tableElement, headerIndex, !currentIsAscending);
          break;
        default:
          break;
      }
    });
  });

  const findValue = (obj, array, j) => {
    let cur = obj[array[0]];
    let new_array = [...array];
    while (new_array.length > 0) {
      cur = obj[new_array[0]];
      obj = cur;
      new_array.shift();
    }
    return cur;
  };

  return (
    <RankingComponent>
      <table className="ranking_table">
        <thead>
          <tr>
            {Object.keys(head).map((i) => (
              <th
                className={
                  toString(head[i].sortable) ||
                  `${head[i].asc ? "asc" : "desc"}`
                }
                key={i}
              >
                <span
                  className="po"
                  style={head[i].sortable ? { cursor: "auto" } : null}
                >
                  {head[i].name}
                </span>
                {head[i].sortable && (
                  <>
                    <i className="fa-solid fa-down-long"></i>
                    <i className="fa-solid fa-up-long"></i>
                  </>
                )}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {Object.keys(body).map((i) => (
            <tr key={i}>
              {Object.keys(head).map((j) => (
                <td key={j}>
                  <span
                    className="td_item"
                    style={
                      head[j].coloredNumber
                        ? body[i][head[j].atribute] > 0
                          ? { color: "rgb(var(--success))" }
                          : { color: "rgb(var(--danger))" }
                        : null
                    }
                  >
                    {Array.isArray(head[j].atribute)
                      ? findValue(body[i], head[j].atribute, j)
                      : body[i][head[j].atribute]}
                  </span>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </RankingComponent>
  );
};

export default Ranking;
