import React, { createContext, useEffect, useState } from "react";
import TodaysMatches from "../components/TodaysMatches";

export const IndexContext = createContext();

function IndexPage() {
  const [matchesData, setMatchesData] = useState({});
  const [curr, setCurr] = useState();

  useEffect(() => {
    fetch("/index")
      .then((res) => res.json())
      .then((data) => setMatchesData(data.matches));
  }, []);

  const follow_match = (id) => {
    useEffect(
      fetch("/follow_match", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ id: id }),
      })
        .then((res) => res.json())
        .then((data) => setCurr(data.match)),
      []
    );

    if (curr === "added") {
      console.log("add");
      setMatchesData((prevData) => ({
        ...prevData,
        [id]: {
          ...prevData[id],
          followed: true,
        },
      }));
    } else if (curr === "deleted") {
      console.log("del");
      setMatchesData((prevData) => ({
        ...prevData,
        [id]: {
          ...prevData[id],
          followed: false,
        },
      }));
    }
    console.log(id);
    console.log(curr);
    console.log("Followed: " + matchesData[id].followed);
  };

  return (
    <IndexContext.Provider value={{ matchesData, follow_match }}>
      {<TodaysMatches />}
    </IndexContext.Provider>
  );
}

export default IndexPage;
