import React, { useState } from "react";
import styled from "styled-components";

const RankingComponent = styled.div`
  .ranking_table .heading {
    display: flex;
    background-color: var(--heading_color);
    margin: 0;
    height: 150px;
    color: #fff;
    padding: 0 10% 0 10%;
    align-items: center;
    justify-content: center;
  }

  .ranking_table .heading img {
    height: 50px;
    margin-right: 20px;
  }

  .ranking_table {
    width: 900px;
    margin: 20px auto 0 auto;
    color: #fff;
    background-color: var(--navbar_color);
    padding: 10px;
  }

  .ranking_table th {
    justify-content: space-between;
    margin: 10px;
    text-align: left;
    user-select: none;
    font-size: 15px;
  }

  .ranking_table .arrows {
    width: 30px;
  }

  .ranking_table th .po {
    cursor: pointer;
    margin-right: 5px;
  }

  .ranking_table th i {
    color: rgba(255, 255, 255, 0.5);
  }

  .ranking_table tbody tr:hover {
    background-color: rgba(var(--secondary), 0.5);
  }

  .ranking_table .td_item {
    display: flex;
    justify-content: center;
    text-align: center;
  }

  .ranking_table #diff {
    justify-content: space-evenly;
  }

  .ranking_table .country {
    user-select: none;
    color: #fff;
  }

  .ranking_table .item {
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .ranking_table .country a {
    padding: 15px;
    border-radius: 5px;
    margin: auto 0 auto 0;
    cursor: pointer;
    user-select: none;
    text-decoration: none;
  }

  .ranking_table .country .country_icon {
    padding-right: 5px;
    padding-top: 2px;
    width: 20px;
    height: 12.5px;
  }
`;

const Ranking = ({ thead, tbody }) => {
  /* 
  
  head={
    0: { name: "Rank", atribute: "position", sortable: false },
    1: { name: "Team", atribute: ["team", "name"], sortable: false },
    2: { name: "Points", atribute: "points", sortable: false },
  }

  */
  const [body, setBody] = useState(tbody);
  const [curColumn, setCurColumn] = useState(thead.default);
  const [order, setOrder] = useState("asc");

  const sorting = (col) => {
    if (order == "asc") {
      const sorted = [...body].sort((a, b) =>
        a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1
      );
    }

    if (curColumn !== col) {
      setCurColumn(col);
      setOrder("asc");
    } else if (curColumn === col) {
      setOrder((prev) => (prev === "asc" ? "desc" : "asc"));
    }
  };

  const findValue = (obj, array, j) => {
    let cur = obj[array[0]];
    let new_array = [...array];
    while (new_array.length > 0) {
      cur = obj[new_array[0]];
      obj = cur;
      new_array.shift();
    }
    return cur;
  };

  return (
    <RankingComponent>
      <table className="ranking_table">
        <thead>
          <tr>
            {Object.keys(thead).map((i) => (
              <th key={i}>
                <span
                  className="po"
                  style={thead[i].sortable ? { cursor: "auto" } : null}
                  onClick={thead[i].sortable ? () => sorting(i) : null}
                >
                  {thead[i].name}
                </span>
                {thead[i].sortable && (
                  <>
                    <i className="fa-solid fa-down-long"></i>
                    <i className="fa-solid fa-up-long"></i>
                  </>
                )}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {body
            ? Object.keys(body).map((i) => (
                <tr key={i}>
                  {Object.keys(thead).map((j) => (
                    <td key={j}>
                      <span
                        className="td_item"
                        style={
                          thead[j].coloredNumber
                            ? body[i][thead[j].atribute] > 0
                              ? { color: "rgb(var(--success))" }
                              : { color: "rgb(var(--danger))" }
                            : null
                        }
                      >
                        {Array.isArray(thead[j].atribute)
                          ? findValue(body[i], thead[j].atribute, j)
                          : body[i][thead[j].atribute]}
                      </span>
                    </td>
                  ))}
                </tr>
              ))
            : null}
        </tbody>
      </table>
    </RankingComponent>
  );
};

export default Ranking;
