import React, { useEffect, useState } from "react";
import styled from "styled-components";
import Button from "../../../components/Button";

const AdminTableComponent = styled.div`
  .ranking_table {
    width: 1200px;
    margin: 20px auto 0 auto;
    color: #fff;
    background-color: var(--navbar_color);
    padding: 10px;
  }

  .ranking_table th {
    justify-content: space-between;
    margin: 10px;
    text-align: left;
    user-select: none;
    font-size: 15px;
    padding-left: 5px;
  }

  .ranking_table th .po {
    margin-right: 5px;
  }

  .ranking_table th i {
    color: rgba(255, 255, 255, 0.5);
  }

  .ranking_table tbody tr:hover {
    .td_item {
      background-color: rgba(var(--secondary), 0.5);
    }
  }

  .ranking_table .td_item {
    display: flex;
    flex-direction: row;
    text-align: center;
    justify-content: center;
    align-items: center;
  }

  .ranking_table .td_item .td_item_img {
    padding-right: 5px;
    width: 20px;
    height: 12.5px;
  }
`;

const InfoComponent = styled.div`
  & {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background-color: var(--navbar_color);
    padding: 50px;
    width: 500px;
    min-width: 350px;
    max-width: 50%;
    border: 2px solid var(--border_light);
    border-radius: 30px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    color: #fff;
  }
`;

const TdItemComponent = styled.span`
  ${(p) =>
    p.head[p.j].coloredNumber
      ? p.body[p.i][p.head[p.j].atribute] > 0
        ? { color: "rgb(var(--success))" }
        : p.body[p.i][p.head[p.j].atribute] === 0
        ? { color: "rgb(var(--secondary))" }
        : { color: "rgb(var(--danger))" }
      : null}
  ${(p) =>
    p.background_colors
      ? {
          backgroundColor: `rgb(${p.background_colors[0]}, ${p.background_colors[1]}, ${p.background_colors[2]}, 0.5)`,
        }
      : null}
`;

const AdminTable = ({ head, tbody, sorting, settings }) => {
  /* 
  
  head={
    0: { 
      name: "Rank", 
      atribute: "position", --where to get info from api
      sortable: true,
      img: --path-to-folder--,
      background: ["color-folder"] }
  }

  sorting={by: atribute, order: "asc"}

  tbody=[{...}, {...}, {...}, ...]

  */

  const [body, setBody] = useState(tbody);

  const [curColumn, setCurColumn] = useState(sorting.by);
  const [order, setOrder] = useState(sorting.order);

  const [showInfo, setShowInfo] = useState(false);

  const sortByColumn = (col) => {
    console.log(body);
    if (curColumn !== col) {
      setCurColumn(col);
      const sorted = [...body].sort((a, b) => (a[col] > b[col] ? 1 : -1));
      setBody(sorted);
      setOrder("desc");
    } else if (order === "asc") {
      const sorted = [...body].sort((a, b) => (a[col] > b[col] ? 1 : -1));
      setBody(sorted);
      setOrder("desc");
    } else if (order === "desc") {
      const sorted = [...body].sort((a, b) => (a[col] < b[col] ? 1 : -1));
      setBody(sorted);
      setOrder("asc");
    }
  };

  const findValue = (obj, array) => {
    if (array) {
      let cur = obj[array[0]];
      let new_array = [...array];
      while (new_array.length > 0 && obj) {
        cur = obj[new_array[0]];
        obj = cur;
        new_array.shift();
      }
      return cur;
    }
  };

  const findLastAtribute = (obj, array) => {
    if (array.length > 1) {
      let cur = obj[array[0]];
      let new_array = [...array];
      while (new_array.length > 1 && obj) {
        cur = obj[new_array[0]];
        obj = cur;
        new_array.shift();
      }
      return cur;
    }
  };

  const deleteItem = (id) => {
    fetch(`${settings.delete}/${id}`)
      .then((res) => res.json())
      .then((d) =>
        d.message === "deleted"
          ? fetch(settings.update)
              .then((res) => res.json())
              .then((r) => setBody(r[settings.data]))
          : console.log(d.message)
      );
  };

  const showUserInfo = () => {
    setShowInfo(true);
  };

  useEffect(() => {
    setBody(tbody);
    setShowInfo(false);
  }, [tbody]);

  const [signupFormData, setSignupFormData] = useState({
    username: "",
    email: "",
    password: "",
    confirm_password: "",
  });

  const [correctInput, setCorrectInput] = useState({
    username: { unique: true, rules: true },
    email: { unique: true, rules: true },
    password: true,
    confirm_password: true,
  });

  const changeData = (event) => {
    const { id, value, type, checked } = event.target;
    setSignupFormData((prevData) => ({
      ...prevData,
      [id]: type === "checkbox" ? checked : value,
    }));
  };

  const createUser = (event) => {
    event.preventDefault();
    fetch(`/register`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(signupFormData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.correct_input) {
          setCorrectInput({
            username: true,
            email: true,
            password: true,
            confirm_password: true,
          });
          login();
          navigate("/");
        } else {
          setCorrectInput(data.data);
        }
      });
  };

  return (
    <AdminTableComponent>
      {!showInfo ? (
        <table className="ranking_table">
          <thead>
            <tr>
              <th>
                <span className="po" style={{ cursor: "auto" }}>
                  Settings
                </span>
              </th>
              {Object.keys(head).map((i) => (
                <th key={i}>
                  <span
                    className="po"
                    style={
                      head[i].sortable
                        ? { cursor: "pointer" }
                        : { cursor: "auto" }
                    }
                    onClick={
                      head[i].sortable
                        ? () => {
                            Array.isArray(head[i].atribute)
                              ? console.log(
                                  findLastAtribute(body[i], head[i].atribute)
                                )
                              : sortByColumn(head[i].atribute);
                          }
                        : null
                    }
                  >
                    {head[i].name}
                  </span>
                  {head[i].sortable && (
                    <>
                      <i
                        className="fa-solid fa-down-long"
                        style={
                          curColumn === head[i].atribute
                            ? order === "desc"
                              ? { color: "#fff" }
                              : null
                            : null
                        }
                      ></i>
                      <i
                        className="fa-solid fa-up-long"
                        style={
                          curColumn === head[i].atribute
                            ? order === "asc"
                              ? { color: "#fff" }
                              : null
                            : null
                        }
                      ></i>
                    </>
                  )}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {body &&
              Object.keys(body).map((i) => (
                <tr key={i}>
                  <td
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      justifyContent: "center",
                      gap: "5px",
                    }}
                  >
                    <Button
                      size="mini"
                      variant="primary"
                      onClick={() => showUserInfo()}
                    >
                      <i class="fa-solid fa-pen-to-square"></i>
                    </Button>
                    <Button
                      size="mini"
                      variant="danger"
                      onClick={() => deleteItem(body[i].id)}
                    >
                      <i class="fa-solid fa-trash"></i>
                    </Button>
                  </td>
                  {Object.keys(head).map((j) => (
                    <td key={j}>
                      <TdItemComponent
                        className="td_item"
                        head={head}
                        body={body}
                        j={j}
                        i={i}
                        background_colors={
                          head[j].background
                            ? findValue(body[i], head[j].background)
                            : null
                        }
                      >
                        {head[j].img ? (
                          <img
                            className="td_item_img"
                            src={`${head[j].img}/${body[i].img}`}
                            alt="img"
                          />
                        ) : null}

                        {Array.isArray(head[j].atribute) ? (
                          findValue(body[i], head[j].atribute)
                        ) : typeof body[i][head[j].atribute] === "boolean" ? (
                          body[i][head[j].atribute] ? (
                            <i
                              style={{ color: "rgb(var(--success))" }}
                              class="fa-solid fa-check"
                            ></i>
                          ) : (
                            <i
                              style={{ color: "rgb(var(--danger))" }}
                              class="fa-solid fa-xmark"
                            ></i>
                          )
                        ) : (
                          body[i][head[j].atribute]
                        )}
                      </TdItemComponent>
                    </td>
                  ))}
                </tr>
              ))}
          </tbody>
        </table>
      ) : (
        <InfoComponent>
          <form className="info_form">
            <div>
              <input
                id="username"
                onChange={changeData}
                placeholder="Username"
                className={
                  "input_box " +
                  correctInput.username.unique +
                  " " +
                  correctInput.username.rules
                }
                type="text"
              />
              <i style={{ color: "#fff" }} className="fa-solid fa-user"></i>
              {!correctInput.username.rules ? (
                <span>Username must be at least 3 letters long.</span>
              ) : !correctInput.username.unique ? (
                <span>User with this username already exists.</span>
              ) : null}
            </div>
            <div
              style={{ marginTop: !correctInput.username ? "10px" : "20px" }}
            >
              <input
                id="email"
                onChange={changeData}
                placeholder="E-Mail"
                className={
                  "input_box " +
                  correctInput.email.unique +
                  " " +
                  correctInput.email.rules
                }
                type="text"
              />
              <i
                id="email_icon"
                style={{ color: "#fff" }}
                className="fa-solid fa-envelope"
              />
              {!correctInput.email.rules ? (
                <span>Email must be valid.</span>
              ) : !correctInput.email.unique ? (
                <span>User with this email already exists.</span>
              ) : null}
            </div>
            <div style={{ marginTop: !correctInput.email ? "10px" : "20px" }}>
              <input
                id="password"
                onChange={changeData}
                placeholder="Password"
                className={"input_box " + correctInput.password}
                type="password"
              />
              <i style={{ color: "#fff" }} className="fa-solid fa-lock"></i>
              {!correctInput.password && (
                <span>
                  Ensure 8-unit password with letters, capitals and numbers.
                </span>
              )}
            </div>
            <div
              style={{ marginTop: !correctInput.password ? "10px" : "20px" }}
            >
              <input
                id="confirm_password"
                onChange={changeData}
                placeholder="Confirm password"
                className={"input_box " + correctInput.confirm_password}
                type="password"
              />
              <i
                id="confirm_icon"
                style={{ color: "#fff" }}
                className="fa-solid fa-lock-open"
              ></i>
              {!correctInput.confirm_password && (
                <span>Confirm Password must be equal to password.</span>
              )}
            </div>
            <Button
              onClick={createUser}
              className="submit_btn"
              variant="light"
              size="xxl"
              style={{
                marginTop: !correctInput.confirm_password ? "10px" : "20px",
              }}
            >
              Submit
            </Button>
          </form>
        </InfoComponent>
      )}
    </AdminTableComponent>
  );
};

export default AdminTable;
