import { useEffect, useState } from "react";

export function useAuth() {
  const [user, setUser] = useState({});
  useEffect(() => {
    fetch("/auth")
      .then((res) => res.json())
      .then((res) => setUser(res.data))
      .catch((e) => console.log(e));
  }, [user.auth]);
  return user;
}

export function useLogin({ formData }) {
  const [auth, setAuth] = useState();
  useEffect(() => {
    fetch("/auth/logout")
      .then((d) => d.kson())
      .then((d) => setAuth(d))
      .catch((e) => console.log(e));
  });
  return auth;
}
