import React, { createContext, useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import Heading from "../../components/Heading";
import styled from "styled-components";

export const MatchDetailsContext = createContext();

const TeamsHeading = styled.div`
  & {
    border-radius: 15px;
    padding: 10px;
    ${(p) =>
      p.color
        ? `background-color: rgba(${p.color[0]}, ${p.color[1]}, ${p.color[2]}, 0.5)`
        : null}
  }
`;

function MatchDetails() {
  const url = useLocation();
  const match_id = url.pathname.slice(15);

  const [match, setMatch] = useState({});
  const [statistics, setStatistics] = useState({});

  useEffect(() => {
    fetch(`/match_details/${match_id}`)
      .then((res) => res.json())
      .then((res) => {
        setMatch(res.match);
        setStatistics(res.statistics);
        console.log(res);
      });
  }, []);

  return (
    <MatchDetailsContext.Provider value={{ match, statistics }}>
      <Heading
        title={
          Object.keys(match).length > 0 && (
            <div>
              <TeamsHeading>{match.teams.home.name}</TeamsHeading>
              <TeamsHeading>{match.teams.away.name}</TeamsHeading>
            </div>
          )
        }
      ></Heading>
    </MatchDetailsContext.Provider>
  );
}

export default MatchDetails;
