import React, { useState, useContext } from "react";
import Button from "./Button";
import { Context } from "../App";
import "./styles/RegisterForm.css";
import { useNavigate } from "react-router-dom";

function RegisterForm() {
  const { login } = useContext(Context);
  const navigate = useNavigate();

  const [signupFormData, setSignupFormData] = useState({
    username: "",
    email: "",
    password: "",
    confirm_password: "",
  });

  const [correctInput, setCorrectInput] = useState({
    username: false,
    email: false,
    password: false,
    confirm_password: false,
  });

  const changeData = (event) => {
    const { id, value, type, checked } = event.target;
    setSignupFormData((prevData) => ({
      ...prevData,
      [id]: type === "checkbox" ? checked : value,
    }));
  };

  const createUser = (event) => {
    event.preventDefault();
    fetch(`/register`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(signupFormData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.correct_input && login()) {
          setCorrectInput(true);
          navigate("/");
        } else {
          setCorrectInput(false);
        }
      });
  };
  return (
    <div className="register_wrapper">
      <h1 className="title">Signup</h1>
      <form className="register_form">
        <div>
          <input
            id="username"
            onChange={changeData}
            placeholder="Username"
            className={"input_box " + correctInput.username}
            type="text"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-user"></i>
        </div>
        <div>
          <input
            id="password"
            onChange={changeData}
            placeholder="E-Mail"
            className={"input_box " + correctInput.email}
            type="text"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-envelope" />
        </div>
        <div>
          <input
            id="password"
            onChange={changeData}
            placeholder="Password"
            className={"input_box " + correctInput.password}
            type="password"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-lock"></i>
        </div>
        <div>
          <input
            id="confirm_password"
            onChange={changeData}
            placeholder="Confirm password"
            className={"input_box " + correctInput.confirm_password}
            type="password"
          />
          <i style={{ color: "#fff" }} className="fa-solid fa-lock-open"></i>
        </div>
        <Button
          onClick={createUser}
          className="login_btn"
          variant="light"
          size="xxl"
        >
          Submit
        </Button>
      </form>
      <p className="ques">
        You already have an account?
        <span className="login" onClick={() => navigate("/login")}>
          Log In
        </span>
      </p>
    </div>
  );
}

export default RegisterForm;
