import React, { useContext, useState, useEffect } from "react";
import "./styles/TodaysMatches.css";
import { IndexContext } from "../pages/IndexPage";

function TodaysMatches() {
  const { matchesData } = useContext(IndexContext);

  console.log(matchesData);

  return (
    <div className="today">
      <h3 className="topping">Today's most interesting mathes</h3>
      {Object.keys(matchesData).map((match, index) => (
        <p style={{ color: "#fff" }} key={index}>
          {match +
            ": " +
            matchesData[match].home +
            " vs " +
            matchesData[match].away}
        </p>
      ))}
      {Object.values(matchesData).map((match, index) => (
        <p style={{ color: "#000000" }} key={index}>
          {match + ": " + match.home + " vs " + match.away}
        </p>
      ))}
    </div>
  );
}

export default TodaysMatches;
