import React from "react";
import styled from "styled-components";

const ButtonComponent = styled.button`
  position: relative;
  dispaly: inline-flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  text-decoration: none;
  vertical-align: middle;
  cursor: pointer;
  user-select: none;
  color: #fff;
  border-radius: 0.6rem;
  transition: all 0.3s;
  padding: ${(props) =>
    props.items === true
      ? "10px"
      : props.size === "sm"
      ? "0 1.5rem; height: 34px; font-size: 16px"
      : props.size === "mid"
      ? "0 1.8rem; height: 37px; font-size: 18px"
      : props.size === "lg"
      ? "0 2rem; height: 40px; font-size: 20px"
      : props.size === "xl"
      ? "0 2.3rem; height: 43px; font-size: 22px"
      : props.size === "xxl"
      ? "0 2.6rem; height: 46px; font-size: 24px"
      : console.log("Button: variant")};
  background-color: rgba(var(--${(props) => props.variant}), 1);
  border: 2px solid rgba(
      ${(props) =>
        props.outline === true
          ? `var(--${props.variant})); background-color: var(--transparent); color: rgb(var(--${props.variant}))`
          : "var(--transparent))"};
  &:hover {
    background-color: rgba(var(--${(props) => props.variant}), 1);
    border-color: rgba(var(--${(props) => props.variant}), 1);
    box-shadow: 0 0 10px 1px rgba(var(--${(props) => props.variant}), 1);
    color: white;
  }
`;

function Button({
  type,
  variant,
  outline = false,
  icon = false,
  className,
  id,
  onClick,
  size,
  children,
}) {
  return (
    <ButtonComponent
      type={type ? type : "button"}
      variant={variant}
      className={className ? `btn_component ${className}` : "btn_component"}
      id={id}
      onClick={onClick}
      size={size}
      outline={outline}
      icon={icon}
    >
      {children}
    </ButtonComponent>
  );
}

export default Button;
